import React, { Component } from "react";
import Counter from "./counter";

class Counters extends Component {
	state = {
		counters: [
			{ id: 1, value: 1 },
			{ id: 2, value: 2 },
			{ id: 3, value: 3 },
			{ id: 4, value: 0 }
		]
	};

	onDelete() {
		console.log("Clickedddd");
	}

	render() {
		return (
			<div>
				{this.state.counters.map(counter => (
					<Counter
						key={counter.id}
						onDelete={this.onDelete}
						value={counter.value}
					/>
				))}
			</div>
		);
	}
}

export default Counters;
